
var assert = require("assert")
var mocha = require("mocha");
var coMocha=require("co-mocha");

coMocha(mocha);

var app = {
    id: 'xxxx',
    sercet: 'xxxx',
    server: ''
};

var shoots = require('../shoots-sdk')(app);

describe('flash test', function() {

	var key='msg';
	var val='i am the flash value';

    it('it should return true', function*() {
        var result = yield shoots.flash.set(key,val);
        assert.equal(true, result);
    });

     it('it should equal the set value', function*() {
        var value = yield shoots.flash.get(key);
        assert.equal(val, value);
    });
});





